mapper.select:
		SELECT
		
		FROM OA_DEVICES
		
			AND LISTNUM like '%'+#{listnum}+'%'
			AND RKTIME >= #{rktime} 
			 AND RKTIME <= #{rktime1} 
			AND LYTIME >= #{lytime} 
			 AND LYTIME <= #{lytime1} 
			AND FHTIME >= #{fhtime} 
			 AND FHTIME <= #{fhtime1} 
			AND STATE = #{state} 
			AND STATE = '3' 
			AND USTATE = #{ustate} 
			 AND LYR LIKE '%'+#{lyr}+'%' 
			AND FIRM LIKE '%'+#{firm}+'%'  
			AND TEST = #{test} 
			AND MODEL LIKE '%'+#{model}+'%'  
			
				AND PACKBM IN
				
					#{item}
				

			
			
				AND DEPARTMENT IN (SELECT ID FROM F_GETC( #{department}))
				
			
			AND CARNUMBER LIKE '%'+#{carNumber}+'%'  
			AND SIMNUMBER LIKE '%'+#{simNumber}+'%'  

			AND INSPECTOR LIKE '%'+#{inspector}+'%'

